mapper.select:
select t1.*,t2.NAME createName from (SELECT *  FROM zlsk_notice  where ststate = 1)t1
left join (SELECT ID,NAME  FROM ss_f_users  where STSTATE = 1) t2 on t1.createrId = t2.ID
where 1=1
and t1.id = #{id}
and t1.title like CONCAT('%',#{title},'%')
order by t1.createtime desc
limit ${pageSize}
offset ${(pageNumber-1)*pageSize}

select t1.*,t2.NAME createName from (SELECT *  FROM zlsk_notice  where ststate = 1)t1
left join (SELECT ID,NAME  FROM ss_f_users  where STSTATE = 1) t2 on t1.createrId = t2.ID
where 1=1
and t1.id != #{id}
and t1.title = #{title}

SELECT count(*)total  FROM zlsk_notice  where ststate = 1
and id = #{id}
and title like CONCAT('%',#{title},'%')
